package com.example.eva3_9_imagen;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.os.Handler;
import android.os.Message;
import android.widget.ImageView;

import java.io.InputStream;
import java.net.URL;

public class MainActivity extends AppCompatActivity {
    ImageView immg;
    Bitmap imb;
    Handler hn = new Handler(){
        @Override
        public void handleMessage(@NonNull Message msg) {
            super.handleMessage(msg);
            immg.setImageBitmap(imb);
        }
    };
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        immg=findViewById(R.id.imgn);
        Thread hilo = new Thread(){
            @Override
            public void run() { super.run();
                imb=cImg("data:image/jpeg;base64,");
                Message msg = hn.obtainMessage();
                hn.sendMessage(msg);
            }
        };hilo.start();
    }
    public Bitmap cImg(String url){
        try { InputStream inputStream = (InputStream) new URL(url).getContent();
            Bitmap bMap = BitmapFactory.decodeStream(inputStream);
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null; }
}